import Vue from "vue";
import VueRouter from "vue-router";
import TableView from "../views/TableView.vue";
import RegisterView from "../views/RegisterView.vue";
import MenuView from "../views/MenuView.vue";
import OrderView from "../views/OrderView.vue";
import ProfileView from "../views/ProfileView.vue";
import AdminUserView from "../views/AdminUserView.vue";
import AdminView from "../views/AdminView.vue";
import AdminCategoryView from "../views/AdminCategoryView.vue";
import AdminTableView from "../views/AdminTableView.vue";
Vue.use(VueRouter);
import store from "./store";
const routes = [
  {
    path: "*",
    redirect: "/register",
  },
  {
    path: "/register",
    name: "register",
    component: RegisterView,
  },
  {
    path: "/home",
    name: "home",
    component: TableView,
    meta: {
      requiresAuth: true,
    },
  },
  {
    path: "/menu",
    name: "menu",
    component: MenuView,
    meta: {
      requiresAuth: true,
    },
  },
  {
    path: "/profile",
    name: "profile",
    component: ProfileView,
    meta: {
      requiresAuth: true,
    },
  },
  {
    path: "/order",
    name: "order",
    component: OrderView,
    meta: {
      requiresAuth: true,
    },
  },
  {
    path: "/admin/food",
    name: "admin",
    component: AdminView,
    meta: {
      requiresAuth: true,
      requiresAdmin: true,
    },
  },
  {
    path: "/admin/user",
    name: "userList",
    component: AdminUserView,
    meta: {
      requiresAuth: true,
      requiresAdmin: true,
    },
  },
  {
    path: "/admin/category",
    name: "categorylist",
    component: AdminCategoryView,
    meta: {
      requiresAuth: true,
      requiresAdmin: true,
    },
  },
  {
    path: "/admin/table",
    name: "tablelist",
    component: AdminTableView,
    meta: {
      requiresAuth: true,
      requiresAdmin: true,
    },
  },
];

const router = new VueRouter({
  mode: "history",
  base: process.env.BASE_URL,
  routes,
});

router.beforeEach(async (to, from, next) => {
  let loginUser = sessionStorage.getItem("loginUser");
  let isLogin = sessionStorage.getItem("isLogin");
  let cc = router.app.store.getters.loginUser;

  console.log(loginUser);
  console.log(isLogin);
  // from - Home
  // to - Profile
  // Need to be login, But is not login
  if (to.meta.requiresAuth == true && !isLogin) {
    next({ path: "/register" });
  }

  // Need to be login, Need to be admin
  else if (
    to.meta.requiresAuth == true &&
    to.meta.requiresAdmin == true &&
    loginUser.userRoles[0].roleName != "ADMIN"
  ) {
    next({ fullPath: "/admin" });
  }

  // If All Okay
  else {
    next();
  }
});
export default router;
