import axios from "axios";

const FOOD_LIST = "http://localhost:8080/food/list";
const GET_FOOD = "http://localhost:8080/food/get/";

export const getAllFoods = ({ commit }) => {
  axios.get(FOOD_LIST).then((response) => {
    commit("SET_FOODS", response.data);
  });
};
export const getFood = ({ commit }, foodId) => {
  axios.get(GET_FOOD`${foodId}`).then((response) => {
    commit("SET_FOOD", response.data);
  });
};
