//       commit("SET_FOOD", response.data);
//     })
//   );
// };

export const addFoodToOrder = ({ commit }, { food, quantity }) => {
  commit("ADD_FOOD_TO_ORDER", { food, quantity });
};
export const removeFoodFromOrder = ({ commit }, food) => {
  commit("REMOVE_FOOD_FROM_ORDER", food);
};
