export const SET_FOODS = (state, foods) => {
  state.foods = foods;
};
// export const SET_FOOD = (state, food) => {
//   state.food = food;
// };

export const ADD_TO_ORDER = (state, { food, quantity }) => {
  //console.log(food);
  let foodInCart = state.cart.find((item) => {
    return item.food.foodId == food.foodId;
  });
  if (foodInCart) {
    foodInCart.quantity += quantity;
    return;
  }
  state.cart.push({
    food,
    quantity,
  });
};
export const setLoginUser = (state, user) => {
  sessionStorage.setItem("loginUser", JSON.stringify(user));
  sessionStorage.setItem("isLogin", true);
  state.loginUser = user;
  state.isLogin = true;
};
export const logout = (state) => {
  sessionStorage.removeItem("loginUser");
  sessionStorage.removeItem("isLogin");
  state.loginUser = {};
  state.isLogin = false;
};
