import Vue from "vue";
import VueRouter from "vue-router";
import AdminView from "../views/AdminView.vue";
Vue.use(VueRouter);

const routes = [
  {
    path: "/",
    name: "menu",
    component: HomeView,
  },
  {
    path: "/admin",
    name: "admin",
    component: AdminView,
  },
];

const router = new VueRouter({
  mode: "history",
  base: process.env.BASE_URL,
  routes,
});

export default router;
